using System;
using System.Collections.Generic;
using System.Configuration;
using System.Drawing;
using System.IO;
using System.Linq;
using System.Xml.Linq;
using TestXML.Business.ViewModel;
using TestXML.Models;

namespace TestXML.Business
{
    public class PersonBusiness : IDisposable
    {
        //Root of XML-file
        private String file = ConfigurationManager.ConnectionStrings["PeopleFile"].ConnectionString;
        private XDocument xDocument = null;
        //Default Image for a person, when image is not Uploaded
        private string defaultImg = "";
       
        // Save a Record to the XML-File
        public int SavePerson(PersonModel person) {

            // Load XML-file
            this.LoadXML();

            // if a Picture is not supplied
            // a default Image is provided
            string imgString = "";
            if (person.Base64String == null) imgString = defaultImg; else imgString = person.Base64String;
            try
            {
                // Create a new XMLNodeElement
                xDocument.Element("People").Add(
                    new XElement("Person", new XAttribute("Id", NextRow()),
                                new XElement("Name", person.Name.ToString()),
                                new XElement("Surname", person.Surname.ToString()),
                                new XElement("Cellphone", person.Cellphone.ToString()),
                                new XElement("Image", imgString)
                    )
                );
                //Save to the XML File
                xDocument.Save(@file);
            }catch(Exception e)
            {
                return -1;
            }
            return NextRow();
        }

        //Search a Record from XML-file using Id
        //Return: PersonModel
        public PersonModel Find(int? id) {

            PersonModel personDetails = null;

            // Make sure the there are records in the XML-File
            LoadXML();
            try {
                //Search for a record from the XML-file using Id
                //This get a XElement/ PeopleNodeElement from the file
                var xmlPerson = xDocument
                    .Element("People")
                    .Elements("Person")
                    .Where(p => p.Attribute("Id").Value == id.ToString()).First();
                    //Populate the Person Model with Person(XElement) data
                    personDetails = new PersonModel(){
                        Id = Convert.ToInt32(xmlPerson.Attribute("Id").Value),
                        Name = xmlPerson.Element("Name").Value.ToString(),
                        Surname = xmlPerson.Element("Surname").Value.ToString(),
                        Cellphone = xmlPerson.Element("Cellphone").Value.ToString(),
                        Image = Convert.FromBase64String(xmlPerson.Element("Image").Value)
                    };
                
            }catch(Exception e)
            {
                Console.WriteLine("InvalidOperationException, " + e.ToString());
            }
            return personDetails;
        }

        //Update XML-file Record
        public PersonModel Update(PersonModel newPerson)
        {
            LoadXML();
            // Search Root for NodeElement(Person) by ID
            var xmlPerson = xDocument
                .Element("People")
                .Elements("Person")
                .Where(p => p.Attribute("Id").Value == newPerson.Id.ToString()).Single();
            if (xmlPerson != null)
            {
                //Update NodeElement with newPerson data
                xmlPerson.Element("Name").Value = newPerson.Name.ToString();
                xmlPerson.Element("Surname").Value = newPerson.Surname.ToString();
                xmlPerson.Element("Cellphone").Value = newPerson.Cellphone.ToString();
                // Only update Image if Image has bee uploaded
                if (newPerson.Base64String != null)
                    xmlPerson.Element("Image").Value = newPerson.Base64String.ToString();
                //Save Changes to the file
                xDocument.Save(@file);
            }
            return newPerson;
        }
        
        //Get All XML-file records
        public List<PersonModel> People(){
            List<PersonModel> people = new List<PersonModel>();
            // Make sure the there are records in the XML-File
            if (NextRow() > 0)
            {
                foreach (XElement xmlPerson in xDocument.Descendants("Person"))
                {
                    people.Add(new PersonModel()
                    {
                        Id = Convert.ToInt32(xmlPerson.Attribute("Id").Value),
                        Name = xmlPerson.Element("Name").Value.ToString(),
                        Surname = xmlPerson.Element("Surname").Value.ToString(),
                        Cellphone = xmlPerson.Element("Cellphone").Value.ToString(),
                        Image = Convert.FromBase64String(xmlPerson.Element("Image").Value.ToString()),
                        Base64String = xmlPerson.Element("Image").Value.ToString(),
                    });
                }
            }

            return people;
        }

        public Boolean DeletePerson(int? id)
        {
            Boolean isDeleted = false;
            if (NextRow() > 0)
            {
                if (Find(id) != null)
                {
                    // Delete record using Id
                    xDocument.Root.Elements().Where(p => p.Attribute("Id").Value == id.ToString()).FirstOrDefault().Remove();
                    isDeleted = true;
                }
                //And save changes
                xDocument.Save(@file);
            }
            return isDeleted;
        }

        // Get Next Row Id
        // This method will always return Id > 0

        private int NextRow()
        {
            int lastIndex = 0;
            if (xDocument == null) { LoadXML(); }// Load xml file

            int lastRowId = xDocument.Element("People").Elements("Person").Count();
            if (lastRowId > 0)
            {
                lastIndex = Convert.ToInt32(xDocument.Element("People").Elements("Person").Last().Attribute("Id").Value); ;
            }
            
            return lastIndex + 1; //Increment Last-Id to create the Next Row-Id
        }

        // Load XML File
        private void LoadXML()
        {
            try{
                // If ezist load file
                //@file =  location of file
                xDocument = XDocument.Load(@file);
            }
            catch (FileNotFoundException fnfe){
                //If file not found 
                //Create a new XML-file
                xDocument = new XDocument(new XDeclaration("1.0", "utf-8", "yes"), new XElement("People"));
            }
        }

        public void Dispose()
        {
        }
    }
}